package models

// Key public part of a key
type Key struct {
	// Secret Value
	PublicKey []byte

	// KeyCryptoAttributes
	Alg *Algo

	// Attr
	Attr *Attributes

	// Metadata generated by the store
	Metadata *Metadata
}

// Algo
type Algo struct {
	// Type of key (e.g. ecdsa, rsa)
	Type string

	// EllipticCurve (e.g secp256k1)
	EllipticCurve string

	// Size of the key
	Size int
}
